mapper.select:


select pr.id,pr.nick, pr.prof_img, (select count(*) from follow where following_id=pr.following_id) following_Cnt
from (
    select 
    tb_user.id id,
    tb_user.nick nick,
    following_id, 
    tb_user.prof_img prof_img, 
    follow_id
    from tb_user, follow where tb_user.id = follow.following_id and follow.follow_id = #{pageId}
    ) pr



select pr.id,pr.nick, pr.prof_img, (select count(*) from follow where following_id=pr.follow_id) following_Cnt
from (
    select 
    tb_user.id id,
    tb_user.nick nick,
    following_id, 
    tb_user.prof_img prof_img, 
    follow_id
    from tb_user, follow where tb_user.id = follow.follow_id and follow.following_id = #{pageId}
    ) pr


select count(*)
from follow
where follow_id = #{follow_id}
and following_id = #{following_id}


select count(follow_ID)
from follow
where following_id = #{following_id}


select count(following_ID)
from follow
where follow_id = #{follow_id}

mapper.delete:

delete from
	follow
where
	follow_id = #{follow_id}
and
	following_id = #{following_id}
