mapper.update:
		update TB_BOARD
        set TITLE = #{TITLE},
        CONTENT = #{CONTENT},
        CATEGORY = #{CATEGORY}
		
		where ID = #{ID} and BOARD_NUM = #{BOARD_NUM};

mapper.select:

	select count(*)
	from TB_FOLLOW
	where follow_id = #{following_id}
	and following_id = #{follow_id}
